import React, { useState } from 'react';
import './CurrencyDropdown.scss';

const CurrencyDropdown = ({ bg }) => {
  const [shown, setShown] = useState(false);

  // const handleClick = () => {
  //   alert(111);
  // };

  return (
    <div className="currency-dropdown">
      <span
        className="currency-dropdown__button"
        onClick={() => setShown(!shown)}
      >
        GBP
      </span>
      {shown && (
        <ul className="currency-dropdown__list">
          <li key="EUR" className="currency-dropdown__item">
            GBP • 0.00
          </li>
          <li key="GBP" className="currency-dropdown__item">
            EUR • 0.00
          </li>
          <li key="USD" className="currency-dropdown__item">
            USD • 0.00
          </li>
        </ul>
      )}
    </div>
  );
};

export default CurrencyDropdown;
