import React, { useState } from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import './CurrencyDropdown.scss';

const CurrencyDropdown = ({ currency, currencies, setCurrency }) => {
  const [shown, setShown] = useState(false);
  const [selectedItem, setSelectedItem] = useState(currency);

  const handleClick = (item) => {
    setSelectedItem(item);
    setShown(false);
    setCurrency(currencies[item].code);
  };

  return (
    <div className="currency-dropdown">
      <span
        className="currency-dropdown__button"
        onClick={() => setShown(!shown)}
      >
        {currency}
      </span>
      <ul
        className={classNames('currency-dropdown__list', {
          'currency-dropdown__list--shown': shown,
        })}
      >
        {currencies.map((c, i) => (
          <li
            key={c.code}
            className={classNames('currency-dropdown__item', {
              'currency-dropdown__item--active': selectedItem === i,
            })}
            onClick={() => handleClick(i)}
          >
            {c.code} â€¢ {c.balance}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default CurrencyDropdown;

CurrencyDropdown.defaultProps = {
  currencies: [],
  currency: '',
  setCurrency: () => {},
};

CurrencyDropdown.propTypes = {
  currencies: PropTypes.array.isRequired,
  currency: PropTypes.string.isRequired,
  setCurrency: PropTypes.func.isRequired,
};
