import React from 'react';
import classNames from 'classnames';
import PropTypes from 'prop-types';
import CurrencyDropdown from '../CurrencyDropdown/CurrencyDropdown';
import './ExchangerRow.scss';

const ExchangerRow = ({
  alt,
  balance,
  currency,
  sign,
  amount,
  currencies,
  onCurrencyChange,
  onAmountChange,
}) => {
  const handleChange = (e) => {
    onAmountChange(e.target.value);
  };

  return (
    <div className={classNames('exchanger-row', { 'exchanger-row--alt': alt })}>
      <div className="exchanger-row__line">
        <CurrencyDropdown
          currency={currency}
          currencies={currencies}
          onCurrencyChange={onCurrencyChange}
        />
        <input
          type="number"
          className="exchanger-row__amount"
          autoComplete="off"
          placeholder="0"
          onChange={handleChange}
          value={amount}
        />
      </div>
      <div className="exchanger-row__line">
        <span className="exchanger-row__balance">
          Balance: {sign}
          {balance}
        </span>
        <span className="exchanger-row__fee">Fee: {'$12,5675.73'}</span>
      </div>
    </div>
  );
};

export default ExchangerRow;

ExchangerRow.defaultProps = {
  alt: false,
  balance: '',
  currency: '',
  sign: '',
  amount: '',
  currencies: [],
  onCurrencyChange: () => {},
  onAmountChange: () => {},
};

ExchangerRow.propTypes = {
  alt: PropTypes.bool.isRequired,
  balance: PropTypes.string.isRequired,
  currency: PropTypes.string.isRequired,
  sign: PropTypes.string.isRequired,
  amount: PropTypes.string.isRequired,
  currencies: PropTypes.array.isRequired,
  onCurrencyChange: PropTypes.func.isRequired,
  onAmountChange: PropTypes.func.isRequired,
};
