import React, { useState } from 'react';
import useDebounce from '../../hooks/useDebounce';
import useFetchData from '../../hooks/useFetchData';
import './ExchangerRow.scss';

const ExchangerRow = () => {
  const [amount, setAmount] = useState('');
  // const [showList, setShowList] = useState(false);
  // const [locations, noResults] = useFetchData(useDebounce(v));

  return (
    <div className="exchanger-row">
      <div className="exchanger-row__column">
        <span className="exchanger-row__currency">GBP</span>
        <span className="exchanger-row__balance">Balance: {'$0'}</span>
      </div>
      <div className="exchanger-row__column">
        <input
          type="text"
          className="exchanger-row__amount"
          autoComplete="off"
          placeholder="0"
          onChange={(e) => setAmount(e.target.value)}
          // onBlur={() => setShowList(false)}
          // onFocus={() => setShowList(true)}
        />
      </div>
    </div>
  );
};

export default ExchangerRow;
