import React, { useState } from 'react';
// import useDebounce from '../../hooks/useDebounce';
// import useFetchData from '../../hooks/useFetchData';
import ExchangerRow from '../ExchangerRow/ExchangerRow';
import './Exchanger.scss';

const Exchanger = () => {
  // const [searchText, setSearchText] = useState('');
  // const [locations, noResults] = useFetchData(useDebounce(searchText));
  const [fromCurrency, setFromCurrency] = useState('GBP');
  const [toCurrency, setToCurrency] = useState('EUR');
  const [fromAmount, setFromAmount] = useState(0);
  const [toAmount, setToAmount] = useState(0);

  // const balances = [{ GBP: '0.00' }, { EUR: '0.00' }, { USD: '0.00' }];

  const currencies = [
    { code: 'GBP', balance: '0.00' },
    { code: 'EUR', balance: '0.00' },
    { code: 'USD', balance: '0.00' },
  ];

  return (
    <div className="exchanger">
      <div className="exchanger__row">
        <ExchangerRow
          currency={fromCurrency}
          currencies={currencies}
          setCurrency={setFromCurrency}
          setAmount={setFromAmount}
          alt
        />
      </div>
      <div className="exchanger__actions">
        <div className="exchanger__actions-container">
          <button className="exchanger__button exchanger__button--swap" />
          <button className="exchanger__button exchanger__button--rates">
            Â£1 = $1.2912
          </button>
          <div className="exchanger__empty-action" />
        </div>
      </div>
      <div className="exchanger__row">
        <ExchangerRow
          currency={toCurrency}
          currencies={currencies}
          setCurrency={setToCurrency}
          setAmount={setToAmount}
        />
      </div>
      <button className="exchanger__submit-button" type="button">
        Exchange
      </button>
    </div>
  );
};

export default Exchanger;
