import React, { useState, useEffect } from 'react';
// import useDebounce from '../../hooks/useDebounce';
import useFetchData from '../../hooks/useFetchData';
import ExchangerRow from '../ExchangerRow/ExchangerRow';
import './Exchanger.scss';

const defaultFromCurrency = 'GBP';
const defaultToCurrency = 'EUR';
const currencies = [
  { code: 'GBP', balance: '10', sign: '£' },
  { code: 'EUR', balance: '0', sign: '€' },
  { code: 'USD', balance: '0', sign: '$' },
];

const Exchanger = () => {
  const [fromCurrency, setFromCurrency] = useState(defaultFromCurrency);
  const [toCurrency, setToCurrency] = useState(defaultToCurrency);
  const [fromRate, toRate] = useFetchData(fromCurrency, toCurrency);
  // console.log(fromRate, toRate);
  const [fromAmount, setFromAmount] = useState(0);
  const [toAmount, setToAmount] = useState(0);
  const [activeCurrency, setActiveCurrency] = useState(defaultFromCurrency);
  // const [fromRate, setFromRate] = useState(1);
  // const [toRate, setToRate] = useState(rate);

  const selectedFromCurrency = currencies.find(
    (item) => item.code === fromCurrency,
  );

  const selectedToCurrency = currencies.find(
    (item) => item.code === toCurrency,
  );

  // const convert = () => {
  //   console.log(
  //     activeCurrency,
  //     selectedFromCurrency.code,
  //     selectedToCurrency.code,
  //   );
  //   if (activeCurrency === selectedFromCurrency.code) {
  //     const exchange = parseFloat((fromAmount * toRate).toFixed(2));
  //     setToAmount(exchange);
  //   }
  //   if (activeCurrency === selectedToCurrency.code) {
  //     const exchange = parseFloat((toAmount / toRate).toFixed(2));
  //     setFromAmount(exchange);
  //   }
  // };

  useEffect(() => {
    if (activeCurrency !== selectedFromCurrency.code) return;
    const exchange = parseFloat((fromAmount * toRate).toFixed(2));
    setToAmount(exchange);
    // convert();
  }, [fromAmount]);

  useEffect(() => {
    if (activeCurrency !== selectedToCurrency.code) return;
    const exchange = parseFloat((toAmount / toRate).toFixed(2));
    setFromAmount(exchange);
    // convert();
  }, [toAmount]);

  // useEffect(() => {
  //   console.log(fromCurrency);
  //   console.log(rate);
  // }, [fromCurrency]);

  // useEffect(() => {
  //   console.log(toCurrency);
  //   console.log(rate);
  // }, [toCurrency]);

  // useEffect(() => {
  // console.log(toCurrency);
  // console.log(rate);
  // convert();
  // }, [rate]);

  useEffect(() => {
    console.log('From Rate:' + fromRate);
    // if (activeCurrency !== selectedFromCurrency.code) return;
    const exchange = parseFloat((fromAmount * toRate).toFixed(2));
    setToAmount(exchange);
  }, [fromRate]);

  useEffect(() => {
    console.log('To Rate:' + toRate);
    // if (activeCurrency !== selectedToCurrency.code) return;
    const exchange = parseFloat((toAmount / toRate).toFixed(2));
    setFromAmount(exchange);
  }, [toRate]);

  return (
    <div className="exchanger">
      <div className="exchanger__row">
        <ExchangerRow
          balance={selectedFromCurrency.balance}
          currency={selectedFromCurrency.code}
          sign={selectedFromCurrency.sign}
          amount={fromAmount}
          currencies={currencies}
          setCurrency={setFromCurrency}
          setAmount={setFromAmount}
          setActiveCurrency={setActiveCurrency}
          alt
        />
      </div>
      <div className="exchanger__actions">
        <div className="exchanger__actions-container">
          <button className="exchanger__button exchanger__button--swap" />
          <button className="exchanger__button exchanger__button--rates">
            {selectedFromCurrency.sign}
            {fromRate} = {selectedToCurrency.sign}
            {toRate}
          </button>
          <div className="exchanger__empty-action" />
        </div>
      </div>
      <div className="exchanger__row">
        <ExchangerRow
          balance={selectedToCurrency.balance}
          currency={selectedToCurrency.code}
          sign={selectedToCurrency.sign}
          amount={toAmount}
          currencies={currencies}
          setCurrency={setToCurrency}
          setAmount={setToAmount}
          setActiveCurrency={setActiveCurrency}
        />
      </div>
      <button className="exchanger__submit-button" type="button">
        Exchange
      </button>
    </div>
  );
};

Exchanger.whyDidYouRender = {
  // logOnDifferentValues: true,
};

export default Exchanger;
