import React, { useState } from 'react';
import './CurrencyDropdown.scss';

const CurrencyDropdown = ({ bg }) => {
  const [shown, setShown] = useState(false);

  const handleClick = () => {
    alert(111);
  };

  return (
    <div className="currency-dropdown">
      <span className="currency-dropdown__button" onClick={handleClick}>
        GBP
      </span>
      <div className="currency-dropdown__list"></div>
    </div>
  );
};

export default CurrencyDropdown;
