import React, { useState, useEffect } from 'react';
import classNames from 'classnames';
import useInterval from '../../hooks/useInterval';
// import useFetchRates from '../../hooks/useFetchRates';
import ExchangerRow from '../ExchangerRow/ExchangerRow';
import './Exchanger.scss';

const defaultFromCurrency = 'GBP';
const defaultToCurrency = 'EUR';

const Exchanger = () => {
  const [fromCurrency, setFromCurrency] = useState(defaultFromCurrency);
  const [toCurrency, setToCurrency] = useState(defaultToCurrency);
  // const rate = useFetchRates(fromCurrency, toCurrency, useInterval(10000));
  // const ratesFetched = useFetchRates(0);
  const interval = useInterval(10000);
  const [rates, setRates] = useState([]);
  // const rate = useFetchRates(fromCurrency, toCurrency);
  // console.log(fromCurrency, toCurrency, rate);
  const [fromAmount, setFromAmount] = useState(0);
  const [toAmount, setToAmount] = useState(0);
  const [isReady, setIsReady] = useState(false);
  // const [activeCurrency, setActiveCurrency] = useState(defaultFromCurrency);
  // const [fromRate, setFromRate] = useState(1);
  // const [toRate, setToRate] = useState(rate);

  const currencies = [
    { code: 'GBP', balance: '10', sign: '£' },
    { code: 'EUR', balance: '0', sign: '€' },
    { code: 'USD', balance: '0', sign: '$' },
  ];

  const selectedFromCurrency = currencies.find(
    (item) => item.code === fromCurrency,
  );

  const selectedToCurrency = currencies.find(
    (item) => item.code === toCurrency,
  );

  const convertFromTo = (currency, amount) =>
    parseFloat((amount * rates[currency]).toFixed(2));

  const convertToFrom = (currency, amount) =>
    parseFloat((amount / rates[currency]).toFixed(2));

  const formatRate = () => {
    return rates[toCurrency] ? parseFloat(rates[toCurrency].toFixed(4)) : null;
  };

  const handleSwap = () => {
    // setFromCurrency(toCurrency);
    // setToCurrency(fromCurrency);
    // setFromAmount(toAmount);
    // setToAmount(fromAmount);
    // // setFromRate(toRate);
    // // setToRate(fromRate);
  };

  const handleFromCurrencyChange = (currency) => {
    setFromCurrency(currency);
    // setToAmount(convertFromTo(currency, fromAmount));
    // setFromAmount(convertToFrom(toAmount));
  };

  const handleToCurrencyChange = (currency) => {
    setToCurrency(currency);
    setToAmount(convertFromTo(currency, fromAmount));
    // setFromAmount(convertToFrom(toAmount));
  };

  const handleFromAmountChange = (amount) => {
    setFromAmount(amount);
    setToAmount(convertFromTo(toCurrency, amount));

    const floatAmount = parseFloat(amount);
    setIsReady(floatAmount > 0 && floatAmount <= selectedFromCurrency.balance);
  };

  const handleToAmountChange = (amount) => {
    setToAmount(amount);
    setFromAmount(convertToFrom(toCurrency, amount));
  };

  const handleExchange = () => {
    currencies[0].balance = 999;
  };

  useEffect(() => {
    const fetchData = async () => {
      const url = `https://openexchangerates.org/api/latest.json?app_id=81fa12e332b74f45a1de4a276db7c0d1&base=${fromCurrency}`;
      const response = await fetch(url);
      const data = await response.json();
      setRates(data.rates);
    };
    fetchData();
  }, [fromCurrency, interval]);

  useEffect(() => {
    setToAmount(convertFromTo(toCurrency, fromAmount));
    // eslint-disable-next-line
  }, [rates]);

  return (
    <div className="exchanger">
      <div className="exchanger__row">
        <ExchangerRow
          balance={selectedFromCurrency.balance}
          currency={selectedFromCurrency.code}
          sign={selectedFromCurrency.sign}
          amount={fromAmount}
          currencies={currencies}
          onAmountChange={handleFromAmountChange}
          onCurrencyChange={handleFromCurrencyChange}
          alt
        />
      </div>
      <div className="exchanger__actions">
        <div className="exchanger__actions-container">
          <button
            className="exchanger__button exchanger__button--swap"
            onClick={handleSwap}
          />
          <button className="exchanger__button exchanger__button--rates">
            {selectedFromCurrency.sign}
            {1} = {selectedToCurrency.sign}
            {rates && formatRate()}
          </button>
          <div className="exchanger__empty-action" />
        </div>
      </div>
      <div className="exchanger__row">
        <ExchangerRow
          balance={selectedToCurrency.balance}
          currency={selectedToCurrency.code}
          sign={selectedToCurrency.sign}
          amount={toAmount}
          currencies={currencies}
          onAmountChange={handleToAmountChange}
          onCurrencyChange={handleToCurrencyChange}
        />
      </div>
      <button
        className={classNames('exchanger__submit-button', {
          'exchanger__submit-button--enabled': isReady,
        })}
        disabled={!isReady}
        type="button"
        onClick={handleExchange}
      >
        Exchange
      </button>
    </div>
  );
};

Exchanger.whyDidYouRender = {
  // logOnDifferentValues: true,
};

export default Exchanger;
