import { useState, useEffect } from 'react';

const useFetchRates = (trigger) => {
  const [rates, setRates] = useState(null);

  useEffect(() => {
    const fetchData = async (from) => {
      const url = `https://openexchangerates.org/api/latest.json?app_id=81fa12e332b74f45a1de4a276db7c0d1&base=${from}`;
      const response = await fetch(url);
      const data = await response.json();

      setRates(data.rates);
    };

    fetchData();
  }, [trigger]);

  return fetchData;
};

export default useFetchRates;
