import React, { useState } from 'react';
import useDebounce from '../../hooks/useDebounce';
import useFetchData from '../../hooks/useFetchData';
// import SearchList from '../SearchList/SearchList';
import './ExchangerRow.scss';

const ExchangerRow = () => {
  const [searchText, setSearchText] = useState('');
  const [showList, setShowList] = useState(false);
  const [locations, noResults] = useFetchData(useDebounce(searchText));

  return (
    <div className="exchanger__row">
      <div className="exchanger__left">
        <label className="exchanger__label">Pick-up Location</label>
      </div>
      <div className="exchanger__right">
        <input
          type="text"
          className="exchanger__input"
          autoComplete="off"
          placeholder="city, airport, station, region, districtâ€¦"
          onChange={(e) => setSearchText(e.target.value)}
          onBlur={() => setShowList(false)}
          onFocus={() => setShowList(true)}
        />
      </div>
    </div>
  );
};

export default ExchangerRow;
