import React, { useState } from 'react';
import './CurrencyDropdown.scss';

const CurrencyDropdown = ({ bg }) => {
  const [shown, setShown] = useState(false);

  // const handleClick = () => {
  //   alert(111);
  // };

  return (
    <div className="currency-dropdown">
      <span
        className="currency-dropdown__button"
        onClick={() => setShown(!shown)}
      >
        GBP
      </span>
      {shown && (
        <div className="currency-dropdown__list">
          <div className="currency-dropdown__item">GBP</div>
          <div className="currency-dropdown__item">EUR</div>
          <div className="currency-dropdown__item">USD</div>
        </div>
      )}
    </div>
  );
};

export default CurrencyDropdown;
