import React, { useState } from 'react';
import PropTypes from 'prop-types';
import useDebounce from '../../hooks/useDebounce';
import useFetchData from '../../hooks/useFetchData';
import CurrencyDropdown from '../CurrencyDropdown/CurrencyDropdown';
import './ExchangerRow.scss';

const ExchangerRow = ({ alt, currencies, setCurrency }) => {
  const [amount, setAmount] = useState('');
  // const [locations, noResults] = useFetchData(useDebounce(v));

  return (
    <div
      className="exchanger-row"
      style={{ backgroundColor: alt ? 'white' : 'transparent' }}
    >
      <div className="exchanger-row__line">
        <CurrencyDropdown currencies={currencies} setCurrency={setCurrency} />
        <input
          type="text"
          className="exchanger-row__amount"
          autoComplete="off"
          placeholder="0"
          onChange={(e) => setAmount(e.target.value)}
        />
      </div>
      <div className="exchanger-row__line">
        <span className="exchanger-row__balance">Balance: {'$0'}</span>
        <span className="exchanger-row__fee">Fee: {'$12,5675.73'}</span>
      </div>
    </div>
  );
};

export default ExchangerRow;

ExchangerRow.defaultProps = {
  alt: false,
  currencies: [],
};

ExchangerRow.propTypes = {
  alt: PropTypes.bool.isRequired,
  currencies: PropTypes.array.isRequired,
};
