import React, { useState, useEffect } from 'react';
import classNames from 'classnames';
import useInterval from '../../hooks/useInterval';
// import useFetchRates from '../../hooks/useFetchRates';
import ExchangerRow from '../ExchangerRow/ExchangerRow';
import ExchangerBar from '../ExchangerBar/ExchangerBar';

import './Exchanger.scss';

const DEFAULT_FROM_CURRENCY = 'GBP';
const DEFAULT_TO_CURRENCY = 'EUR';
const EXCHANGE_FEE = 0.005;
const MIMIMUM_AMOUNT = 0.1;
const ALLOWANCE_LIMIT = 1000;
const FETCH_INTERVAL = 10000000; //10000;
const BALANCES = [
  { code: 'GBP', balance: '10', sign: '£' },
  { code: 'EUR', balance: '0', sign: '€' },
  { code: 'USD', balance: '0', sign: '$' },
];

const useApi = () => {
  const convertFromTo = (amount, rate) => {
    const conversion = ((amount || 0) * rate).toFixed(2);
    // eslint-disable-next-line
    return conversion == 0 ? '' : conversion;
  };

  const convertToFrom = (amount, rate) => {
    const conversion = ((amount || 0) / rate).toFixed(2);
    // eslint-disable-next-line
    return conversion != 0 ? '' : conversion;
  };

  const calculate = () => {
    const floatAmount = parseFloat(amount);
    const fee = (floatAmount * EXCHANGE_FEE).toFixed(2);
    const isAmountExceeding = floatAmount > parseFloat(currency.balance);
    const isMinAmountReached = !amount || floatAmount >= MIMIMUM_AMOUNT;
    const isFeeInvolved = floatAmount > ALLOWANCE_LIMIT;
    const isAllowedToExchange =
      isAmountExceeding || !isMinAmountReached || !amount;
    const feeInfo = `Fee: ${currency.sign}${fee}`;
    const exceedInfo = 'exceeds balance';
    const minInfo = `minimum amount is ${currency.sign}${MIMIMUM_AMOUNT.toFixed(
      2,
    )}`;
    const toInfo = isFeeInvolved ? feeInfo : '';
    const fromInfo = !isMinAmountReached
      ? minInfo
      : isAmountExceeding
      ? exceedInfo
      : '';

    return { isAllowedToExchange, fromInfo, toInfo };
  };

  return {
    convertFromTo,
    convertToFrom,
  };
};

const Exchanger = () => {
  const [fromCurrency, setFromCurrency] = useState(DEFAULT_FROM_CURRENCY);
  const [toCurrency, setToCurrency] = useState(DEFAULT_TO_CURRENCY);
  const interval = useInterval(FETCH_INTERVAL);
  const [rates, setRates] = useState(null);
  const [fromAmount, setFromAmount] = useState();
  const [toAmount, setToAmount] = useState();
  const [isExceeding, setIsExceeding] = useState(true);
  const [fromInfo, setFromInfo] = useState('');
  const [toInfo, setToInfo] = useState('');
  const [currencies, setCurrencies] = useState(BALANCES);
  const api = useApi();

  const selectedFromCurrency = currencies.find((c) => c.code === fromCurrency);
  const selectedToCurrency = currencies.find((c) => c.code === toCurrency);

  const convertFromTo = (currency, amount) =>
    rates ? api.convertFromTo(amount, rates[currency]) : '';

  const convertToFrom = (currency, amount) =>
    rates ? api.convertToFrom(amount, rates[currency]) : '';

  const calculate = (amount, currency) => {
    // const floatAmount = parseFloat(amount);
    // const fee = (floatAmount * EXCHANGE_FEE).toFixed(2);
    // const isAmountExceeding = floatAmount > parseFloat(currency.balance);
    // const isMinAmountReached = !amount || floatAmount >= MIMIMUM_AMOUNT;
    // const isFeeInvolved = floatAmount > ALLOWANCE_LIMIT;
    // const isAllowedToExchange =
    //   isAmountExceeding || !isMinAmountReached || !amount;
    // const feeInfo = `Fee: ${currency.sign}${fee}`;
    // const exceedInfo = 'exceeds balance';
    // const minInfo = `minimum amount is ${currency.sign}${MIMIMUM_AMOUNT.toFixed(
    //   2,
    // )}`;
    // const toInfo = isFeeInvolved ? feeInfo : '';
    // const fromInfo = !isMinAmountReached
    //   ? minInfo
    //   : isAmountExceeding
    //   ? exceedInfo
    //   : '';

    setIsExceeding(isAllowedToExchange);
    setFromInfo(fromInfo);
    setToInfo(toInfo);
  };

  const handleSwap = () => {
    setFromCurrency(toCurrency);
    setToCurrency(fromCurrency);
    setFromAmount(toAmount);
    setToAmount(fromAmount);
    calculate(toAmount, selectedToCurrency);
  };

  const handleFromCurrencyChange = (currency) => {
    if (currency === toCurrency) {
      setToCurrency(fromCurrency);
      calculate(toAmount, selectedToCurrency);
    }

    setFromCurrency(currency);
  };

  const handleToCurrencyChange = (currency) => {
    if (currency === fromCurrency) {
      setFromCurrency(toCurrency);
      calculate(toAmount, selectedToCurrency);
    }

    setToCurrency(currency);
    setToAmount(convertFromTo(currency, fromAmount));
  };

  const handleFromAmountChange = (amount) => {
    setFromAmount(amount);
    setToAmount(convertFromTo(toCurrency, amount));
    calculate(amount, selectedFromCurrency);
  };

  const handleToAmountChange = (amount) => {
    setToAmount(amount);
    setFromAmount(convertToFrom(toCurrency, amount));
    calculate(amount, selectedFromCurrency);
  };

  const handleExchange = () => {
    const newCurrencies = currencies.map((item) => {
      if (item.code === fromCurrency) {
        const newBalance = parseFloat(item.balance) - parseFloat(fromAmount);
        return { ...item, balance: newBalance.toFixed(2) };
      } else if (item.code === toCurrency) {
        const newBalance = parseFloat(item.balance) + parseFloat(toAmount);
        return { ...item, balance: newBalance.toFixed(2) };
      } else {
        return item;
      }
    });

    setCurrencies(newCurrencies);
    setFromAmount();
    setToAmount();
    setIsExceeding(false);
  };

  useEffect(() => {
    const fetchData = async () => {
      const url = `https://openexchangerates.org/api/latest.json?app_id=81fa12e332b74f45a1de4a276db7c0d1&base=${fromCurrency}`;
      const response = await fetch(url);
      const data = await response.json();
      setRates(data.rates);
    };

    fetchData();
  }, [fromCurrency, interval]);

  useEffect(() => {
    setToAmount(convertFromTo(toCurrency, fromAmount));
    // eslint-disable-next-line
  }, [rates]);

  return (
    <div className="exchanger">
      <div className="exchanger__row">
        <ExchangerRow
          balance={selectedFromCurrency.balance}
          currency={selectedFromCurrency.code}
          sign={selectedFromCurrency.sign}
          amount={fromAmount}
          info={fromInfo}
          currencies={currencies}
          onAmountChange={handleFromAmountChange}
          onCurrencyChange={handleFromCurrencyChange}
          autoFocus
          alt
        />
      </div>
      <div className="exchanger__bar">
        <ExchangerBar
          onSwap={handleSwap}
          fromSign={selectedFromCurrency.sign}
          toSign={selectedToCurrency.sign}
          rate={rates && rates[toCurrency]}
        />
      </div>
      <div className="exchanger__row">
        <ExchangerRow
          balance={selectedToCurrency.balance}
          currency={selectedToCurrency.code}
          sign={selectedToCurrency.sign}
          amount={toAmount}
          info={toInfo}
          currencies={currencies}
          onAmountChange={handleToAmountChange}
          onCurrencyChange={handleToCurrencyChange}
        />
      </div>
      <button
        className={classNames('exchanger__submit-button', {
          'exchanger__submit-button--enabled': !isExceeding,
        })}
        disabled={isExceeding}
        type="button"
        onClick={handleExchange}
      >
        Exchange
      </button>
    </div>
  );
};

Exchanger.whyDidYouRender = {
  // logOnDifferentValues: true,
};

export default Exchanger;
