import React, { useState } from 'react';
import useDebounce from '../../hooks/useDebounce';
import useFetchData from '../../hooks/useFetchData';
import ExchangerRow from '../ExchangerRow/ExchangerRow';
import './Exchanger.scss';

const Exchanger = () => {
  const [searchText, setSearchText] = useState('');
  const [showList, setShowList] = useState(false);
  const [locations, noResults] = useFetchData(useDebounce(searchText));

  return (
    <div className="exchanger">
      <ExchangerRow locations={locations} noResults={noResults} />
      <div className="exchanger__actions">
        <button className="exchanger__swap-button" />
        <button className="exchanger__rates-button" />
      </div>
      <ExchangerRow locations={locations} noResults={noResults} />
    </div>
  );
};

export default Exchanger;
